import com.wuweibi.module4j.ModuleUtilsimport com.wuweibi.module4j.ModuleActivator;import com.wuweibi.module4j.module.ModuleContextimport org.codehaus.groovy.tools.GroovyClass;/** * 栏目模块  *  * @author marker */public class ModuleActivatorImpl implements ModuleActivator {		 	/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception {		Class clzz = require("src/a");        GroovyObject obj = clzz.newInstance();        Object str = obj.invokeMethod("getInfo",null);        println(str);        println "module start"	} 			/**	 * 停用	 */	public void stop(ModuleContext context) throws Exception {        println "module stop"	}}